export function isTransient(e){if(!e)return!1;const t=e.toLowerCase();return t.includes("overloaded")||t.includes("rate limit")||t.includes("too many requests")||t.includes("quota")||t.includes("temporarily unavailable")||t.includes("unavailable")||t.includes("try again later")}export function getSync(e){return new Promise(t=>{try{chrome.storage.sync.get(e,e=>t(e||{}))}catch{t({})}})}export function getLocal(e){return new Promise(t=>{try{chrome.storage.local.get(e,e=>t(e||{}))}catch{t({})}})}export function setLocal(e){try{chrome.storage.local.set(e)}catch{}}